mapper.select:
select B.id, B.USERNAME username, B.NAME name, B.BIRTH birth, B.PHONE phone, B.EMAIL email, B.CUSTOMER_RANK_ID "customerRank.type"
from GIFT_CUSTOMERS A, CUSTOMERS B
A.CUSTOMER_ID = B.ID
and username like #{keyword}
and name like #{keyword}
UNION
select B.id, B.USERNAME username, B.NAME name, B.BIRTH birth, B.PHONE phone, B.EMAIL email, B.CUSTOMER_RANK_ID "customerRank.type"
from COUPON_Customers A, CUSTOMERS B
A.CUSTOMER_ID = B.ID
and username like #{keyword}
and name like #{keyword}

select A.id, A.USERNAME username, A.NAME name, A.BIRTH birth, A.PHONE phone, A.EMAIL email, A.CUSTOMER_RANK_ID "customerRank.type", B.coupon_id "coupon.id"
from CUSTOMERS A, coupon_customers B
A.id = B.CUSTOMER_ID and B.coupon_id like #{keyword}

select A.id, A.USERNAME username, A.NAME name, A.BIRTH birth, A.PHONE phone, A.EMAIL email, A.CUSTOMER_RANK_ID "customerRank.type", B.GIFT_ID "gift.id"
from CUSTOMERS A, GIFT_CUSTOMERS B
A.id = B.CUSTOMER_ID and B.GIFT_ID like #{keyword}

mapper.delete:
		DELETE from
    		CUSTOMER_TYPES
		where
   			id in (
   				
   					${a}
   				
   			)
